var (
	PUB_CERT = ""

	// PUB_CERT = "-----BEGIN CERTIFICATE----------END CERTIFICATE-----"
)

func decode_certificate(cert []byte) {

	// pubCert, err := x509.ParsePKIXPublicKey(cert)

	pubCert, err := x509.ParseDERCRL(cert)

	if err != nil {
		fmt.Println("Cert err :", err)
	}

	fmt.Println("Version: ", pubCert)
}

func main() {

	// VerifyPEMCertInfo([]byte(PUB_CERT))

	// ExampleCertificate_Verify()

	decode_certificate([]byte(PUB_CERT))

	// decode_certificate(PUB_CERT)
}
